mapper.select:
select IFNULL(
(select id
from customer
where phone =
#{mobile}
), 0)

select IFNULL(
(select id
from customer
where login_name =
#{loginName}
), 0)

mapper.update:
update customer
set
pwd = #{password}
where phone = #{mobile}

mapper.select:
SELECT c.*,w.money_amount as moneyAmount,w.wallet_pwd as
walletPwd,w.id as walletId FROM `customer` c left JOIN wallet w on c.ID = w.customer_id
where c.ID = #{id}
