mapper.update:
update shop
set shop.isDelete = 1
where shop.id = #{id}

update shop
set shop.status = 0
where shop.id = #{id}

mapper.select:
select shop.id, shop.`name`, shop.establishTime, shop.kind, shop.status, shop.img, shop.tag ,shop.description from shop
shop.id like concat('%',#{id},'%')
and shop.`name` like ('%',#{name},'%')
and shop.status = #{status}
and shop.establishTime between #{beginTime} and #{endTime}
and shop.isDelete = 0
